import React from 'react'
import "./Sidebar.css"
import SidebarOption from '../SidebarOption/SidebarOption'
import HomeIcon from '@material-ui/icons/Home';
import SearchIcon from '@material-ui/icons/Search';
import ExitToAppIcon from '@material-ui/icons/ExitToApp';
import LibraryMusicIcon from '@material-ui/icons/LibraryMusic';
import { useDataLayerValue } from '../../DataLayer/DataLayer';
import { NavLink } from 'react-router-dom';
import GitHubIcon from '@material-ui/icons/GitHub';

function Sidebar() {
    const [{playlists}, dispatch] = useDataLayerValue()

    return (
        <div className="sidebar">
            <img className="sidebar__logo" src="https://storage.googleapis.com/pr-newsroom-wp/1/2018/11/Spotify_Logo_CMYK_Green.png" alt="" />
        <NavLink to="/bets" exact={true} style={{ color: "inherit", textDecoration: 'inherit'}}>
            <SidebarOption Icon={HomeIcon} title="Home"/>
        </NavLink>
        <a target="_blank" rel="noopener noreferrer" href="https://github.com/MattMores/Sportify">
        <SidebarOption Icon={GitHubIcon} title="GitHub Repo" />
        </a>
        <NavLink to="/splash" exact={true} style={{ color: "inherit", textDecoration: 'inherit'}}>
            <SidebarOption Icon={LibraryMusicIcon} title="Spotify Auth" />
        </NavLink>
        <br />
        <strong className="sidebar__title">PLAYLISTS</strong>
        {/* hr = horizantal row line break */}
        <hr />
        {playlists?.items?.map(playlist => (
        <SidebarOption key={playlist.id} title={playlist.name} />
        ))}
        <SidebarOption title={"Rock"} />
        <SidebarOption title={"R&B"} />
        </div>
    )
}

export default Sidebar
