import React, { useState } from 'react';
import { Modal } from '../../context/Modal';
import LoginForm from './LoginForm';
import SignupFormPage from './LogSignUp';
import "./LoginForm.css"

function LoginFormModal() {
  const [isLoginActive, setLoginActive] = useState(true);
  const current = isLoginActive ? "Sign Up" : "Log In";
  const [classList, setClassList] = useState("right");
  const click = () => {
    if (isLoginActive) {
      setClassList("left");
    } else {
      setClassList("right");
    }
    setLoginActive(!isLoginActive)
  }

  const [showModal, setShowModal] = useState(false);

  return (
    <>
      <div className="login__modal" onClick={() => setShowModal(true)}>Log In To Sportify</div>
      {showModal && (
        <Modal onClose={() => setShowModal(false)}>
          <LoginForm />
        </Modal>
      )}
    </>
  );
}

export default LoginFormModal;
